mapper.select:
SELECT COUNT(*) FROM CHAT_ROOM_JOIN
WHERE CHAT_ROOM_NO = #{chatRoomNo}
AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE FROM CHAT_ROOM_JOIN
WHERE MEMBER_NO = #{memberNo}
AND CHAT_ROOM_NO = #{chatRoomNo}

mapper.update:
UPDATE CHAT_ROOM SET
STATUS = 'N'
WHERE CHAT_ROOM_NO = #{chatRoomNo}
